import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

const CountdownTimer = ({ timeRemaining, gameState }) => {
  const [displayTime, setDisplayTime] = useState('00:00');
  const [progress, setProgress] = useState(0);
  const [localTimeRemaining, setLocalTimeRemaining] = useState(timeRemaining);
  const [lastUpdateTime, setLastUpdateTime] = useState(null);
  const totalTime = 5 * 60 * 1000; // 5 minutes in ms

  // Update local time when server time changes - ensure proper reset
  useEffect(() => {
    if (timeRemaining && timeRemaining > 0 && timeRemaining <= totalTime) {
      setLocalTimeRemaining(timeRemaining);
      setLastUpdateTime(Date.now());
    } else if (timeRemaining === 0 || !timeRemaining) {
      setLocalTimeRemaining(0);
      setProgress(0);
    }
  }, [timeRemaining, totalTime]);

  // Smooth countdown using client-side timer
  useEffect(() => {
    if (gameState === 'waiting' && localTimeRemaining > 0) {
      const interval = setInterval(() => {
        setLocalTimeRemaining(prev => {
          if (prev <= 1000) {
            clearInterval(interval);
            return 0;
          }
          return prev - 1000;
        });
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [gameState, localTimeRemaining]);

  useEffect(() => {
    if (localTimeRemaining && localTimeRemaining > 0) {
      // Calculate display time
      const totalSeconds = Math.floor(localTimeRemaining / 1000);
      const minutes = Math.floor(totalSeconds / 60);
      const seconds = totalSeconds % 60;

      setDisplayTime(`${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`);

      // Calculate progress (0-100) - reset to 0 when new countdown starts
      // Use the actual time remaining as the basis for calculation
      const elapsed = totalTime - localTimeRemaining;
      const progressPercent = (elapsed / totalTime) * 100;
      setProgress(Math.min(100, Math.max(0, progressPercent)));
    } else {
      setDisplayTime('00:00');
      setProgress(gameState === 'spinning' ? 100 : 0);
    }
  }, [localTimeRemaining, gameState, totalTime]);

  const getTimerColor = () => {
    if (gameState === 'spinning') return '#f59e0b';
    if (timeRemaining < 30000) return '#ef4444'; // Red for last 30 seconds
    if (timeRemaining < 60000) return '#f59e0b'; // Yellow for last minute
    return '#00d4ff'; // Blue for normal
  };

  const getStatusText = () => {
    switch (gameState) {
      case 'spinning': return 'SPINNING...';
      case 'processing': return 'PROCESSING...';
      case 'completed': return 'COMPLETED';
      case 'paused': return 'PAUSED';
      default: return 'NEXT SPIN IN';
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      className="flex flex-col items-center"
    >
      {/* Status Text - Compact */}
      <div className="text-center mb-2">
        <div className="text-gray-400 text-xs font-medium mb-1">
          {getStatusText()}
        </div>
        {gameState === 'waiting' && (
          <div className="text-white text-lg font-bold font-cyber">
            {displayTime}
          </div>
        )}
      </div>

      {/* Circular Progress - Compact */}
      <div className="relative w-24 h-24 mb-2">
        <CircularProgressbar
          value={progress}
          styles={buildStyles({
            pathColor: getTimerColor(),
            trailColor: 'rgba(255, 255, 255, 0.1)',
            strokeLinecap: 'round',
            pathTransitionDuration: 1,
          })}
        />

        {/* Center Content - Compact */}
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-center">
            {gameState === 'spinning' ? (
              <motion.div
                animate={{ rotate: 360 }}
                transition={{ duration: 1, repeat: Infinity, ease: 'linear' }}
                className="text-lg"
              >
                ðŸŽ°
              </motion.div>
            ) : gameState === 'processing' ? (
              <motion.div
                animate={{ scale: [1, 1.2, 1] }}
                transition={{ duration: 1, repeat: Infinity }}
                className="text-lg"
              >
                âš¡
              </motion.div>
            ) : (
              <div className="text-white text-sm font-bold font-mono">
                {displayTime}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Additional Info - Compact */}
      <div className="text-center">
        <div className="glass px-3 py-1 rounded-full">
          <span className="text-gray-400 text-xs">
            {gameState === 'waiting' ? 'Auto-spin 5min' :
             gameState === 'spinning' ? 'Selecting...' :
             gameState === 'processing' ? 'Processing...' :
             'Updated'}
          </span>
        </div>
      </div>
    </motion.div>
  );
};

export default CountdownTimer;
